<?php /** @var $bar \GDO\UI\GDT_Bar **/$bar->addClass('ui-corner-all gdt-bar flx flx-' . $bar->htmlDirection());if ($bar->wrap){    $bar->addClass('flx-wrap');}?><?php if ($bar->fields) : ?><div <?=$bar->htmlAttributes()?> data-role="controlgroup" <?=$bar->flexDirection==='row'?'data-type="horizontal"':''?>>  <?php foreach ($bar->fields as $field) : ?>	<?=$field->renderCell()?>  <?php endforeach; ?></div><?php endif;?>